import api from './api';

export const pedidoService = {
  // Listar pedidos
  async getPedidos(params = {}) {
    const response = await api.get('/pedidos', { params });
    return response;
  },

  // Buscar pedido por ID
  async getPedidoById(id) {
    const response = await api.get(`/pedidos/${id}`);
    return response;
  },

  // Criar novo pedido
  async createPedido(data) {
    const response = await api.post('/pedidos', data);
    return response;
  },

  // Atualizar pedido
  async updatePedido(id, data) {
    const response = await api.put(`/pedidos/${id}`, data);
    return response;
  },

  // Atualizar status do pedido
  async updateStatusPedido(id, status) {
    const response = await api.patch(`/pedidos/${id}/status`, { status });
    return response;
  },

  // Deletar pedido
  async deletePedido(id) {
    const response = await api.delete(`/pedidos/${id}`);
    return response;
  },

  // Relatório de vendas
  async getRelatorioVendas(params = {}) {
    const response = await api.get('/pedidos/relatorio', { params });
    return response;
  }
};
