import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { ArrowLeft, Edit, Trash2, Calendar, User, Building, Package } from 'lucide-react';
import pedidoService from '../../services/pedidoService';
import clienteService from '../../services/clienteService';
import empresaService from '../../services/empresaService';
import produtoService from '../../services/produtoService';
import LoadingSpinner from '../../components/LoadingSpinner/LoadingSpinner';
import './PedidoView.css';

const PedidoView = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [pedido, setPedido] = useState(null);
  const [cliente, setCliente] = useState(null);
  const [empresa, setEmpresa] = useState(null);
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPedido();
  }, [id]);

  const fetchPedido = async () => {
    try {
      setLoading(true);
      const pedidoResponse = await pedidoService.getById(id);
      // Como o interceptor já retorna response.data, verificamos se há uma propriedade data
      const pedidoData = pedidoResponse.data || pedidoResponse;
      setPedido(pedidoData);

      // Buscar dados relacionados
      if (pedidoData.cliente) {
        try {
          const clienteResponse = await clienteService.getById(pedidoData.cliente);
          const clienteData = clienteResponse.data || clienteResponse;
          setCliente(clienteData);
        } catch (error) {
          console.error('Erro ao buscar cliente:', error);
        }
      }

      if (pedidoData.empresa) {
        try {
          const empresaResponse = await empresaService.getById(pedidoData.empresa);
          const empresaData = empresaResponse.data || empresaResponse;
          setEmpresa(empresaData);
        } catch (error) {
          console.error('Erro ao buscar empresa:', error);
        }
      }

      // Buscar produtos
      if (pedidoData.produtos && pedidoData.produtos.length > 0) {
        try {
          const produtoIds = pedidoData.produtos.map(p => p.produto);
          const produtosResponse = await produtoService.getAll();
          const produtosData = produtosResponse.produtos || produtosResponse.data || produtosResponse;
          setProdutos(Array.isArray(produtosData) ? produtosData : []);
        } catch (error) {
          console.error('Erro ao buscar produtos:', error);
          setProdutos([]);
        }
      }
    } catch (error) {
      console.error('Erro ao buscar pedido:', error);
      // Remover alert duplicado - o interceptor já mostra o toast
      if (error.response?.status !== 401) {
        console.error('Erro detalhado:', error.response?.data || error.message);
      }
      navigate('/pedidos');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir este pedido?')) {
      try {
        await pedidoService.delete(id);
        // Remover alert duplicado - o interceptor já mostra o toast
        navigate('/pedidos');
      } catch (error) {
        console.error('Erro ao excluir pedido:', error);
        // Remover alert duplicado - o interceptor já mostra o toast
      }
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('pt-BR');
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pendente: { label: 'Pendente', class: 'status-pendente' },
      confirmado: { label: 'Confirmado', class: 'status-confirmado' },
      em_preparacao: { label: 'Em Preparação', class: 'status-preparacao' },
      enviado: { label: 'Enviado', class: 'status-enviado' },
      entregue: { label: 'Entregue', class: 'status-entregue' },
      cancelado: { label: 'Cancelado', class: 'status-cancelado' }
    };

    const config = statusConfig[status] || { label: status, class: 'status-default' };
    return (
      <span className={`status-badge ${config.class}`}>
        {config.label}
      </span>
    );
  };

  const getProdutoNome = (produtoId) => {
    const produto = produtos.find(p => (p._id || p.id) === produtoId);
    return produto ? produto.nome : 'Produto não encontrado';
  };

  if (loading) {
    return <LoadingSpinner message="Carregando pedido..." />;
  }

  if (!pedido) {
    return (
      <div className="pedido-view-page">
        <div className="error-message">
          <h2>Pedido não encontrado</h2>
          <Link to="/pedidos" className="btn btn-primary">
            Voltar para Pedidos
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="pedido-view-page">
      <div className="page-header">
        <div className="page-title-section">
          <button 
            className="btn-back"
            onClick={() => navigate('/pedidos')}
          >
            <ArrowLeft size={20} />
          </button>
          <div>
            <h1 className="page-title">
              Pedido #{pedido.numero || pedido._id || pedido.id}
            </h1>
            <p className="page-subtitle">
              Detalhes do pedido
            </p>
          </div>
        </div>
        <div className="action-buttons">
          <Link 
            to={`/pedidos/editar/${pedido._id || pedido.id}`}
            className="btn btn-outline"
          >
            <Edit size={16} />
            Editar
          </Link>
          <button 
            className="btn btn-danger"
            onClick={handleDelete}
          >
            <Trash2 size={16} />
            Excluir
          </button>
        </div>
      </div>

      <div className="page-content">
        <div className="pedido-details">
          <div className="details-grid">
            <div className="detail-card">
              <div className="detail-header">
                <Building size={20} />
                <h3>Empresa</h3>
              </div>
              <div className="detail-content">
                <p>{empresa ? (empresa.nomeFantasia || empresa.razaoSocial) : 'Carregando...'}</p>
              </div>
            </div>

            <div className="detail-card">
              <div className="detail-header">
                <User size={20} />
                <h3>Cliente</h3>
              </div>
              <div className="detail-content">
                <p>{cliente ? cliente.nome : 'Carregando...'}</p>
                {cliente && cliente.telefone && (
                  <p className="detail-secondary">Tel: {cliente.telefone}</p>
                )}
              </div>
            </div>

            <div className="detail-card">
              <div className="detail-header">
                <Calendar size={20} />
                <h3>Data</h3>
              </div>
              <div className="detail-content">
                <p>{formatDate(pedido.data)}</p>
              </div>
            </div>

            <div className="detail-card">
              <div className="detail-header">
                <Package size={20} />
                <h3>Status</h3>
              </div>
              <div className="detail-content">
                {getStatusBadge(pedido.status)}
              </div>
            </div>
          </div>

          {pedido.observacao && (
            <div className="detail-card full-width">
              <div className="detail-header">
                <h3>Observações</h3>
              </div>
              <div className="detail-content">
                <p>{pedido.observacao}</p>
              </div>
            </div>
          )}

          <div className="detail-card full-width">
            <div className="detail-header">
              <Package size={20} />
              <h3>Produtos</h3>
            </div>
            <div className="detail-content">
              <div className="produtos-table">
                <table>
                  <thead>
                    <tr>
                      <th>Produto</th>
                      <th>Quantidade</th>
                      <th>Valor Unitário</th>
                      <th>Total</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pedido.produtos && pedido.produtos.map((item, index) => (
                      <tr key={index}>
                        <td>{getProdutoNome(item.produto)}</td>
                        <td>{item.quantidade}</td>
                        <td>{formatCurrency(item.valorUnitario || 0)}</td>
                        <td>{formatCurrency(item.valorTotal || 0)}</td>
                      </tr>
                    ))}
                  </tbody>
                  <tfoot>
                    <tr>
                      <td colSpan="3"><strong>Total do Pedido:</strong></td>
                      <td><strong>{formatCurrency(pedido.valorTotal || 0)}</strong></td>
                    </tr>
                  </tfoot>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PedidoView;
